// Aggregate all connected issues - handle merge node output
const mergedData = input.merged || input;
const data = Array.isArray(mergedData) ? mergedData : [mergedData];

// Separate car info from issues
let carInfo = null;
const issues = [];

data.forEach(item => {
    if (item.car && item.track) {
        carInfo = item;
    } else if (item.issueType) {
        issues.push(item);
    }
});

// Build structured issue summary
const issuesSummary = issues.map((issue, idx) => {
    return `${idx + 1}. ${issue.issueType.toUpperCase()} - ${issue.cornerPhase} phase (${issue.cornerSpeed} speed corners)
   Description: ${issue.description}
   Symptoms: ${issue.symptoms}`;
}).join('\n\n');

// Create the analysis context
const analysisContext = {
    car: carInfo?.car || 'Unknown Car',
    track: carInfo?.track || 'Unknown Track',
    conditions: carInfo?.conditions || 'Unknown',
    currentSetup: carInfo?.currentSetup || 'Baseline',
    sessionType: carInfo?.sessionType || 'Practice',
    tireCompound: carInfo?.tireCompound || 'Unknown',
    fuelLoad: carInfo?.fuelLoad || 'Unknown',
    issueCount: issues.length,
    issues: issues,
    issuesSummary: issuesSummary
};

return analysisContext;

